#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const ll P = 1e9+7;
int t=1;
ll f[137][14];
ll qpow(ll x,ll a){
    ll res=1;
    while(a){
        if(a&1)res=res*x%P;
        x= x*x%P;
        a>>=1;
    }
    return res;
}
void Init() {
    for(int j=1;j<=13;j+=2){
        for(int i=1;i<=136;i++){
            if(j==1)f[i][j]=(1+(3*j)*qpow(i,P-2)%P*f[i-1][j-1]%P+(i-3*j)*qpow(i,P-2)%P*f[i-1][j]%P)%P;
            else    f[i][j]=(1+(3*j)*qpow(i,P-2)%P*f[i-1][j-2]%P+(i-3*j)*qpow(i,P-2)%P*f[i-1][j]%P)%P;
        }
    }
}
void solve(){
    char s[26];
    scanf("%s",&s);
    int pp[5][10];
    for(int i=1;i<5;i++){
        for(int j=1;j<10;j++){
            pp[i][j]=0;
        }
    }
    int cnt=0;
    for(int i=0;i<26;i+=2){
        switch(s[i+1]){
        case 'm':pp[1][s[i]-'0']++;break;
        case 'p':pp[2][s[i]-'0']++;break;
        case 's':pp[3][s[i]-'0']++;break;
        case 'z':pp[4][s[i]-'0']++;break;
        default:break;
        }
    }
    for(int i=1;i<5;i++){
        for(int j=1;j<10;j++){
            if(pp[i][j]>1)cnt++;
        }
    }
    printf("Case #%d: %lld\n",t++,f[123][13-cnt*2]);
}
int main(){
    Init();
    int T;
    cin>>T;
    while(T--)solve();
    return 0;
}
